import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEnvelope, faBell, faCog, faMapMarkerAlt, faBriefcase, faImage, faPaperclip, faFileAudio, faUserPlus, faSignOutAlt, faChevronDown } from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import axios from 'axios';
import moment from 'moment'; // Import moment.js
import './Socialize.css';

const Socialize = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const { user } = useSelector(state => state.auth);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/posts/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, []);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleSignOut = () => {
    console.log("Sign out button clicked");
  };

  const handlePostSubmit = async () => {
    if (post.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/api/posts/create', 
          {
            message: post,
            firstName: user?.firstName,
            lastName: user?.lastName,
            email: user?.email,
            picturePath: user?.picturePath // Include the user's picturePath
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts([...posts, response.data]);
        setPost('');
      } catch (error) {
        console.error('Error creating post:', error);
      }
    }
  };

 

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} className="top-icon" />
          <FontAwesomeIcon icon={faBell} className="top-icon" />
          <FontAwesomeIcon icon={faCog} className="top-icon" />
          <div className="user-dropdown">
            <span className="user-name" onClick={toggleDropdown}>
              {user?.firstName}
              <FontAwesomeIcon icon={faChevronDown} className="dropdown-icon" />
            </span>
            {dropdownOpen && (
              <div className="dropdown-menu">
                <button onClick={handleSignOut} className="signout-button">
                  <FontAwesomeIcon icon={faSignOutAlt} /> Sign Out
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
      <div id="mid-social">
        <div className="profile-card">
          <img src={user?.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="profile-image" />
          <h2>{user?.firstName} {user?.lastName}</h2>
          <p>{user?.friends?.length} friends</p>
          <div className="location">
            <FontAwesomeIcon icon={faMapMarkerAlt} /> {user?.location}
          </div>
          <div className="job-title">
            <FontAwesomeIcon icon={faBriefcase} /> {user?.occupation}
          </div>
          <div className="social-profiles">
            <div className="social-profile">
              <FontAwesomeIcon icon={faTwitter} /> Twitter
            </div>
            <div className="social-profile">
              <FontAwesomeIcon icon={faLinkedin} /> LinkedIn
            </div>
          </div>
        </div>
        <div className='post'>
          <div className='postMessage'>
            <div className='message'>
              <img src={user?.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="post-image" />
              <input
                type="text"
                placeholder="What's on your mind..."
                value={post}
                onChange={handlePostChange}
                id="add-post"
              />
            </div>
            <div className='post-icons'>
              <div className='icon-container'>
                <FontAwesomeIcon icon={faImage} className="post-icon" />
                <FontAwesomeIcon icon={faPaperclip} className="post-icon" />
                <FontAwesomeIcon icon={faFileAudio} className="post-icon" />
              </div>
              <button className="post-button" onClick={handlePostSubmit}>Post</button>
            </div>
          </div>
          <div className='posts-list'>
            {posts.map((post, index) => (
              <div key={index} className='post-item'>
                {post.picturePath && <img src={`http://localhost:5000/${post.picturePath}`} alt="Post" className="post-image" />}
                <div className='post-content'>
                  <div className='post-header'>
                    <img src={post.picturePath ? `http://localhost:5000/${post.picturePath}` : '/default-profile.png'} alt="User" className="post-image" />
                    <strong>{post.firstName} {post.lastName}</strong>
                    <span className="post-time">{(post.createdAt)}</span>
                  </div>
                  <p className='post-text'>{post.message}</p>
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className='add-list'>
          <h2>People you may know</h2>
          <div className='add'>
            <img src="/image.png" alt="Profile" className="add-image" />
            <div className="add-info">
              <h3>hahaha</h3>
              <p>Islamabad, Pakistan</p>
            </div>
            <FontAwesomeIcon icon={faUserPlus} className="add-icon" />
          </div>
          <div className='add'>
            <img src="/unnamed.jpg" alt="Profile" className="add-image" />
            <div className="add-info">
              <h3>raz</h3>
              <p>Karachi, Pakistan</p>
            </div>
            <FontAwesomeIcon icon={faUserPlus} className="add-icon" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Socialize;
