import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEnvelope, faBell, faCog, faUser, faMapMarkerAlt, faBriefcase, faSignOutAlt, faThumbsUp, faComment, faShare, faUserPlus, faChevronDown } from '@fortawesome/free-solid-svg-icons';
import axios from 'axios';
import moment from 'moment';
import './Socialize.css';
import { logout } from '../../actions/authActions';

const Socialize = () => {
  const [receivedRequests, setReceivedRequests] = useState([]);
  const [requestStatus, setRequestStatus] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [friendsDropdownOpen, setFriendsDropdownOpen] = useState(false);
  const [comment, setComment] = useState('');
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [sentRequests, setSentRequests] = useState([]);

  const { user } = useSelector(state => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleIconClick = () => {
    navigate('/chat', { state: { currentUser: user } });
  };

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/posts/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        const filteredUsers = response.data
          .filter(u => u._id !== user._id)
          .slice(0, 5);
        setUsers(filteredUsers);
        setFilteredUsers(filteredUsers);

        const requestsResponse = await axios.get('http://localhost:5000/api/friendRequests/sent', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setSentRequests(requestsResponse.data.map(req => req.receiver.toString()));
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchPosts();
    fetchUsers();
    fetchReceivedRequests();
  }, [user._id]);

  const fetchReceivedRequests = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/friendRequests/', {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });

      if (Array.isArray(response.data.receivedRequests)) {
        setReceivedRequests(response.data.receivedRequests);
      } else {
        console.error('Received requests data is not an array:', response.data.receivedRequests);
        setReceivedRequests([]);
      }

      if (Array.isArray(response.data.sentRequests)) {
        setSentRequests(response.data.sentRequests.map(req => req.receiver.toString()));
      } else {
        console.error('Sent requests data is not an array:', response.data.sentRequests);
        setSentRequests([]);
      }
    } catch (error) {
      console.error('Error fetching friend requests:', error);
      setReceivedRequests([]);
      setSentRequests([]);
    }
  };

  const acceptFriendRequest = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5000/api/friendRequests/accept/${requestId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchReceivedRequests();
    } catch (error) {
      console.error('Error accepting friend request:', error);
    }
  };

  const rejectFriendRequest = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5000/api/friendRequests/reject/${requestId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchReceivedRequests();
    } catch (error) {
      console.error('Error rejecting friend request:', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    if (event.target.value.trim()) {
      setFilteredUsers(users.filter(user =>
        `${user.firstName} ${user.lastName}`.toLowerCase().includes(event.target.value.toLowerCase())
      ));
    } else {
      setFilteredUsers([]);
    }
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const toggleFriendsDropdown = () => {
    setFriendsDropdownOpen(!friendsDropdownOpen);
  };

  const handleSignOut = () => {
    dispatch(logout());
    navigate('/signin');
  };

  const sendFriendRequest = async (receiverId) => {
    try {
      await axios.post('http://localhost:5000/api/friendRequests/send',
        { receiverId },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
    } catch (error) {
      console.error('Error sending friend request:', error);
    }
  };

  const handlePostSubmit = async () => {
    if (post.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/api/posts/create',
          {
            message: post,
            firstName: user?.firstName,
            lastName: user?.lastName,
            email: user?.email,
            picturePath: user?.picturePath
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts([...posts, response.data]);
        setPost('');
      } catch (error) {
        console.error('Error creating post:', error);
      }
    }
  };

  const handleLike = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/like/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleCommentSubmit = async (postId) => {
    if (comment.trim()) {
      try {
        const response = await axios.post(`http://localhost:5000/api/posts/comment/${postId}`,
          {
            userId: user?._id,
            text: comment
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts(posts.map(post => post._id === postId ? response.data : post));
        setComment('');
      } catch (error) {
        console.error('Error commenting on post:', error);
      }
    }
  };

  const handleShare = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/share/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error sharing post:', error);
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div id="input-container">
          <input type="text" placeholder="Search..." value={searchTerm} onChange={handleSearchChange} />
          <FontAwesomeIcon icon={faSearch} />
          <FontAwesomeIcon icon={faEnvelope} onClick={handleIconClick} />
          <FontAwesomeIcon icon={faBell} />
          <FontAwesomeIcon icon={faUser} onClick={toggleFriendsDropdown} />
          <FontAwesomeIcon icon={faCog} />
          <FontAwesomeIcon icon={faSignOutAlt} onClick={handleSignOut} />
          {friendsDropdownOpen && (
            <div className="dropdown-content">
              <div>
                <h3>Friend Requests</h3>
                <ul>
                  {receivedRequests.map(request => (
                    <li key={request._id}>
                      <span>{request.sender.firstName} {request.sender.lastName}</span>
                      <button onClick={() => acceptFriendRequest(request._id)}>Accept</button>
                      <button onClick={() => rejectFriendRequest(request._id)}>Reject</button>
                    </li>
                  ))}
                </ul>
              </div>
            </div>
          )}
        </div>
      </div>
      <div id="left-social">
        <div className="profile-container">
          <div className="profile-img-container">
            <img src={`http://localhost:5000/assets/${user?.picturePath}`} alt="Profile" />
          </div>
          <h3>{user?.firstName} {user?.lastName}</h3>
          <p className="username">@{user?.firstName} {user?.lastName}</p>
          <div className="profile-details">
            <p><FontAwesomeIcon icon={faMapMarkerAlt} /> Islamabad, Pakistan</p>
            <p><FontAwesomeIcon icon={faBriefcase} /> Frontend Engineer</p>
          </div>
        </div>
        <div className="trending">
          <h3>People you may know</h3>
          <ul>
            {filteredUsers.length > 0 ? (
              filteredUsers.map(user => (
                <li key={user._id}>
                  <div className="profile-img-container">
                    <img src={`http://localhost:5000/assets/${user.picturePath}`} alt="Profile" />
                  </div>
                  <div className="user-details">
                    <span>{user.firstName} {user.lastName}</span>
                    <button
                      onClick={() => {
                        sendFriendRequest(user._id);
                        setSentRequests([...sentRequests, user._id]);
                      }}
                      disabled={sentRequests.includes(user._id)}
                    >
                      {sentRequests.includes(user._id) ? 'Request Sent' : 'Send Request'}
                    </button>
                  </div>
                </li>
              ))
            ) : (
              <p>No users found</p>
            )}
          </ul>
        </div>
      </div>
      <div id="center-social">
        <div className="post-container">
          <div className="post-input-container">
            <textarea placeholder="What's on your mind?" value={post} onChange={handlePostChange}></textarea>
            <button onClick={handlePostSubmit}>Post</button>
          </div>
          <div className="post-list">
            {posts.map(post => (
              <div key={post._id} className="post">
                <div className="post-header">
                  <div className="post-header-img-container">
                    <img src={`http://localhost:5000/assets/${post.picturePath}`} alt="Profile" />
                  </div>
                  <div className="post-header-details">
                    <span className="post-author">{post.firstName} {post.lastName}</span>
                    <span className="post-time">{moment(post.date).fromNow()}</span>
                  </div>
                </div>
                <div className="post-content">
                  <p>{post.message}</p>
                </div>
                <div className="post-actions">
                  <FontAwesomeIcon icon={faThumbsUp} onClick={() => handleLike(post._id)} />
                  <FontAwesomeIcon icon={faComment} />
                  <FontAwesomeIcon icon={faShare} onClick={() => handleShare(post._id)} />
                </div>
                <div className="comments-container">
                  <div className="comment-input-container">
                    <textarea placeholder="Write a comment..." value={comment} onChange={handleCommentChange}></textarea>
                    <button onClick={() => handleCommentSubmit(post._id)}>Comment</button>
                  </div>
                  <div className="comment-list">
                    {post.comments.map(comment => (
                      <div key={comment._id} className="comment">
                        <div className="comment-header">
                          <div className="comment-header-img-container">
                            <img src={`http://localhost:5000/assets/${comment.userId.picturePath}`} alt="Profile" />
                          </div>
                          <div className="comment-header-details">
                            <span className="comment-author">{comment.userId.firstName} {comment.userId.lastName}</span>
                            <span className="comment-time">{moment(comment.date).fromNow()}</span>
                          </div>
                        </div>
                        <div className="comment-content">
                          <p>{comment.text}</p>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
      <div id="right-social">
        <div className="news-container">
          <h3>News</h3>
          <ul>
            <li><FontAwesomeIcon icon={faTwitter} /> Check out this tweet!</li>
            <li><FontAwesomeIcon icon={faLinkedin} /> Follow us on LinkedIn!</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Socialize;
