import {
    SIGNUP_SUCCESS,
    SIGNUP_FAIL,
    SIGNIN_SUCCESS,
    SIGNIN_FAIL,
    LOGOUT
  } from '../actions/types';
  
  const initialState = {
    token: localStorage.getItem('token'),
    isAuthenticated: false,
    loading: true,
    user: null,
    error: null
  };
  
  export default function (state = initialState, action) {
    const { type, payload } = action;
  
    switch (type) {
      case SIGNUP_SUCCESS:
      case SIGNIN_SUCCESS:
        localStorage.setItem('token', payload.token);
        return {
          ...state,
          isAuthenticated: true,
          loading: false,
          error: null
        };
      case SIGNUP_FAIL:
      case SIGNIN_FAIL:
      case LOGOUT:
        localStorage.removeItem('token');
        return {
          ...state,
          token: null,
          isAuthenticated: false,
          loading: false,
          user: null,
          error: payload
        };
      default:
        return state;
    }
  }
