import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { signup } from '../actions/authActions';
import './Signup.css';

const Signup = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    location: '',
    occupation: '',
    picturePath: '',
  });

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { error, isAuthenticated } = useSelector((state) => state.auth);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, picturePath: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await dispatch(signup(formData)); // Wait for signup action to complete

    if (isAuthenticated) {
      navigate('/signin'); // Navigate to signin page if authenticated
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/signin');
    }
  }, [isAuthenticated, navigate]);

  return (
    <div id="outerContainer">
      <div id="signupContainer">
        <h1 id="header">Sociopedia</h1>
        <h2 id="subtitle">Welcome to Sociopedia, the Social Media For Sociopaths!</h2>
        {error && <p id="error">{error}</p>}
        <form onSubmit={handleSubmit} id="form">
          <div id="row">
            <input
              type="text"
              placeholder="First Name"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              id="input"
              required
            />
            <input
              type="text"
              placeholder="Last Name"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              id="input"
              required
            />
          </div>
          <input
            type="text"
            placeholder="Location"
            name="location"
            value={formData.location}
            onChange={handleChange}
            id="input"
          />
          <input
            type="text"
            placeholder="Occupation"
            name="occupation"
            value={formData.occupation}
            onChange={handleChange}
            id="input"
          />
          <div id="fileUpload">
            <input
              type="file"
              onChange={handleFileChange}
              id="fileInput"
            />
            <button type="button" onClick={() => document.getElementById('fileInput').click()} id="uploadButton">
            </button>
            <span id="filePath">{formData.picturePath}</span>
          </div>
          <input
            type="email"
            placeholder="Email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            id="input"
            required
          />
          <input
            type="password"
            placeholder="Password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            id="input"
            required
          />
          <button type="submit" id="button"><a href="/signin" id="loginLink">REGISTER </a></button>
        </form>
        <p id="loginText">Already have an account? <a href="/signin" id="loginLink">Login here.</a></p>
      </div>
    </div>
  );
};

export default Signup;
