import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Chat.css';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);

  useEffect(() => {
    // Fetch users from the backend
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/all'); // Adjust the API endpoint as needed
        setUsers(response.data);
        console.log(response.data)
      } catch (err) {
        console.error('Error fetching users:', err);
      }
    };

    fetchUsers();
  }, []);

  const chatData = [
    { id: 1, sender: 'Robin Sparkles', message: 'Hi Tec👋', time: '52 minutes ago' },
    { id: 2, sender: 'Robin Sparkles', message: 'hey🙂', time: '38 minutes ago' },
  ];

  return (
    <div className="chat-container">
      <div className="sidebar">
        <div className="search-container">
          <input type="text" placeholder="Search" className="search-input" />
          <span className="search-icon">🔍</span>
        </div>
        <div className="chats-section">
        <h2>You</h2>
        //user.name
        //img


          <h2>Chats</h2>
          {users.map((user) => (
            <div key={user._id} className="chat-item">
              <img src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="add-image" />
              <span>{user.firstName} {user.lastName}</span>
            </div>
          ))}
        </div>
      </div>
      <div className="chat-area">
        <div className="chat-header">
          <h1>Chat App</h1>
          <div className="header-icons">
            <span className="icon">🌙</span>
            <span className="icon">⚙️</span>
          </div>
        </div>
        <div className="messages">
          {chatData.map((chat) => (
            <div key={chat.id} className={`message ${chat.sender === 'You' ? 'sent' : 'received'}`}>
              <div className="message-content">
                <p>{chat.message}</p>
                <span className="message-time">{chat.time}</span>
              </div>
            </div>
          ))}
        </div>
        <div className="input-area">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Write a message..."
            className="message-input"
          />
          <button className="send-button">
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
