import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEnvelope, faBell, faCog, faMapMarkerAlt, faBriefcase, faImage, faPaperclip, faFileAudio, faUserPlus } from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import './Socialize.css';
import { fetchUserData, fetchPosts } from '../actions/userActions'; // Add these actions

const Socialize = () => {
  const [post, setPost] = useState('');
  
  const dispatch = useDispatch();
  const { user, posts, peopleYouMayKnow } = useSelector((state) => state.user); // Access user and posts from Redux state

  useEffect(() => {
    dispatch(fetchUserData());
    dispatch(fetchPosts());
  }, [dispatch]);

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const handlePostSubmit = async (event) => {
    event.preventDefault();
    // Dispatch action to add post
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} className="top-icon" />
          <FontAwesomeIcon icon={faBell} className="top-icon" />
          <FontAwesomeIcon icon={faCog} className="top-icon" />
          <span className="user-name">{user ? user.firstName : 'User'}</span>
        </div>
      </div>
      <div id="mid-social">
        <div className="profile-card">
          <img src={user ? user.picturePath : '/image.png'} alt="Profile" className="profile-image" />
          <h2>{user ? user.firstName : 'Fake Man'}</h2>
          <p>{user ? `${user.friends.length} friends` : '2 friends'}</p>
          <div className="location">
            <FontAwesomeIcon icon={faMapMarkerAlt} /> {user ? user.location : 'Fake Location, CA'}
          </div>
          <div className="job-title">
            <FontAwesomeIcon icon={faBriefcase} /> {user ? user.occupation : 'Fake Con Man'}
          </div>
          
          <div className="social-profiles">
            <div className="social-profile">
              <FontAwesomeIcon icon={faTwitter} /> Twitter
            </div>
            <div className="social-profile">
              <FontAwesomeIcon icon={faLinkedin} /> LinkedIn
            </div>
          </div>
        </div>
        <div className='post'>
          <div className='message'>
            <img src={user ? user.picturePath : '/image.png'} alt="Profile" className="post-image" />
            <input
              type="text"
              placeholder="What's on your mind..."
              value={post}
              onChange={handlePostChange}
              id="add-post"
            />
          </div>
          <div className='post-icons'>
            <div className='icon-container'>
              <FontAwesomeIcon icon={faImage} className="post-icon" />
              <FontAwesomeIcon icon={faPaperclip} className="post-icon" />
              <FontAwesomeIcon icon={faFileAudio} className="post-icon" />
            </div>
            <button className="post-button" onClick={handlePostSubmit}>Post</button>
          </div>
        </div>
        <div className='add-list'>
          <h2>People you may know</h2>
          {peopleYouMayKnow && peopleYouMayKnow.map(person => (
            <div className='add' key={person.id}>
              <img src={person.picturePath || "/image.png"} alt="Profile" className="add-image" />
              <div className="add-info">
                <h3>{person.firstName}</h3>
                <p>{person.location}</p>
              </div>
              <FontAwesomeIcon icon={faUserPlus} className="add-icon" />
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default Socialize;
