import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import axios from 'axios';
import './Socialize.css'; // Ensure you have the corresponding CSS file for styles

const Socialize = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const { user } = useSelector(state => state.auth);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleSignOut = () => {
    // Implement sign-out functionality
    console.log("Sign out button clicked");
  };

  const handlePostSubmit = async () => {
    try {
      await axios.post('http://localhost:5000/api/posts/create', 
        {
          message: post,
          firstName: user.firstName,
          lastName: user.lastName,
          email: user.email
        },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setPost(''); // Clear the post field after successful submission
    } catch (error) {
      console.error('Error creating post:', error);
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <div className="search-bar">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
          />
        </div>
        <div className="dropdown-container">
          <button onClick={toggleDropdown} className="dropdown-button">
            Menu
          </button>
          {dropdownOpen && (
            <div className="dropdown-menu">
              <button onClick={handleSignOut}>Sign Out</button>
              {/* Add more dropdown options here */}
            </div>
          )}
        </div>
      </div>
      <div id="mid-social">
        <div className='post'>
          <div className='message'>
            {user && (
              <img
                src={`http://localhost:5000/${user.picturePath}`}
                alt="Profile"
                className="post-image"
              />
            )}
            <input
              type="text"
              placeholder="What's on your mind..."
              value={post}
              onChange={handlePostChange}
              id="add-post"
            />
          </div>
          <div className='post-icons'>
            <div className='icon-container'>
              {/* Add icons for different post actions */}
            </div>
            <button className="post-button" onClick={handlePostSubmit}>Post</button>
          </div>
        
          <div className='post-icons'>
            <div className='icon-container'>
              <FontAwesomeIcon icon={faImage} className="post-icon" />
              <FontAwesomeIcon icon={faPaperclip} className="post-icon" />
              <FontAwesomeIcon icon={faFileAudio} className="post-icon" />
            </div>
            <button className="post-button">Post</button>
          </div>
        </div>
        <div className='add-list'>
          <h2>People you may know</h2>
          <div className='add'>
            <img src="/image.png" alt="Profile" className="add-image" />
            <div className="add-info">
              <h3>hahaha</h3>
              <p>Islamabad, Pakistan</p>
            </div>
            <FontAwesomeIcon icon={faUserPlus} className="add-icon" />
          </div>
          <div className='add'>
            <img src="/unnamed.jpg" alt="Profile" className="add-image" />
            <div className="add-info">
              <h3>raz</h3>
              <p>Karachi, Pakistan</p>
            </div>
            <FontAwesomeIcon icon={faUserPlus} className="add-icon" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Socialize;
