import React, { useState } from 'react';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEnvelope, faBell, faCog, faMapMarkerAlt, faBriefcase, faImage, faPaperclip, faFileAudio, faUserPlus, faSignOutAlt, faChevronDown } from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import './Socialize.css';

const Socialize = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);  // State to store posts
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const { user } = useSelector(state => state.auth);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleSignOut = () => {
    // Implement sign-out functionality
    console.log("Sign out button clicked");
  };

  const handlePostSubmit = () => {
    if (post.trim()) {
      setPosts([...posts, { text: post, user: user.firstName }]); // Add new post
      setPost(''); // Clear the input field
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} className="top-icon" />
          <FontAwesomeIcon icon={faBell} className="top-icon" />
          <FontAwesomeIcon icon={faCog} className="top-icon" />
          <div className="user-dropdown">
            <span className="user-name" onClick={toggleDropdown}>
              {user && user.firstName}
              <FontAwesomeIcon icon={faChevronDown} className="dropdown-icon" />
            </span>
            {dropdownOpen && (
              <div className="dropdown-menu">
                <button onClick={handleSignOut} className="signout-button">
                  <FontAwesomeIcon icon={faSignOutAlt} /> Sign Out
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
      <div id="mid-social">
        <div className="profile-card">
          <img src={user && `http://localhost:5000/${user.picturePath}`} alt="Profile" className="profile-image" />
          <h2>{user && `${user.firstName} ${user.lastName}`}</h2>
          <p>{user && user.friends.length} friends</p>
          <div className="location">
            <FontAwesomeIcon icon={faMapMarkerAlt} /> {user && user.location}
          </div>
          <div className="job-title">
            <FontAwesomeIcon icon={faBriefcase} /> {user && user.occupation}
          </div>
          <div className="social-profiles">
            <div className="social-profile">
              <FontAwesomeIcon icon={faTwitter} /> Twitter
            </div>
            <div className="social-profile">
              <FontAwesomeIcon icon={faLinkedin} /> LinkedIn
            </div>
          </div>
        </div>
        <div className='post'>
          <div className='message'>
            <img src={user && `http://localhost:5000/${user.picturePath}`} alt="Profile" className="post-image" />
            <input
              type="text"
              placeholder="    What's on your mind..."
              value={post}
              onChange={handlePostChange}
              id="add-post"
            />
          </div>
          <div className='post-icons'>
            <div className='icon-container'>
              <FontAwesomeIcon icon={faImage} className="post-icon" />
              <FontAwesomeIcon icon={faPaperclip} className="post-icon" />
              <FontAwesomeIcon icon={faFileAudio} className="post-icon" />
            </div>
            <button className="post-button" onClick={handlePostSubmit}>Post</button>
          </div>
        </div>
        <div className='posts-list'>
          {posts.map((post, index) => (
            <div key={index} className='post-item'>
              <strong>{post.user}:</strong> {post.text}
            </div>
          ))}
        </div>
        <div className='add-list'>
          <h2>People you may know</h2>
          <div className='add'>
            <img src="/image.png" alt="Profile" className="add-image" />
            <div className="add-info">
              <h3>hahaha</h3>
              <p>Islamabad, Pakistan</p>
            </div>
            <FontAwesomeIcon icon={faUserPlus} className="add-icon" />
          </div>
          <div className='add'>
            <img src="/unnamed.jpg" alt="Profile" className="add-image" />
            <div className="add-info">
              <h3>raz</h3>
              <p>Karachi, Pakistan</p>
            </div>
            <FontAwesomeIcon icon={faUserPlus} className="add-icon" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Socialize;
