import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEnvelope, faBell, faCog, faUser, faMapMarkerAlt, faBriefcase, faImage, faPaperclip, faFileAudio, faUserPlus, faSignOutAlt, faChevronDown, faThumbsUp, faComment, faShare } from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import axios from 'axios';
import moment from 'moment';
import './Socialize.css';
import { logout } from '../actions/authActions'; // Adjust the path if necessary

const Socialize = () => {
  const [receivedRequests, setReceivedRequests] = useState([]);
  const [requestStatus, setRequestStatus] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [friendsDropdownOpen, setFriendsDropdownOpen] = useState(false);
  const [comment, setComment] = useState('');
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [sentRequests, setSentRequests] = useState([]);

  const { user } = useSelector(state => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate(); // Hook for navigation

  const handleIconClick = () => {
    navigate('/chat', { state: { currentUser: user } });
  };

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/posts/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        const filteredUsers = response.data
          .filter(u => u._id !== user._id)
          .slice(0, 5);
        setUsers(filteredUsers);
        setFilteredUsers(filteredUsers);
    
        // Fetch sent friend requests
        const requestsResponse = await axios.get('http://localhost:5000/api/friendRequests/sent', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setSentRequests(requestsResponse.data.map(req => req.receiver.toString()));
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };
    
    fetchPosts();
    fetchUsers();
    fetchReceivedRequests();
  }, [user._id]);

  const fetchReceivedRequests = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/friendRequests/', {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
  
      // Ensure that receivedRequests and sentRequests are always arrays
      if (Array.isArray(response.data.receivedRequests)) {
        setReceivedRequests(response.data.receivedRequests);
      } else {
        console.error('Received requests data is not an array:', response.data.receivedRequests);
        setReceivedRequests([]);
      }
  
      if (Array.isArray(response.data.sentRequests)) {
        setSentRequests(response.data.sentRequests.map(req => req.receiver.toString()));
      } else {
        console.error('Sent requests data is not an array:', response.data.sentRequests);
        setSentRequests([]);
      }
    } catch (error) {
      console.error('Error fetching friend requests:', error);
      setReceivedRequests([]);
      setSentRequests([]);
    }
  };

  const acceptFriendRequest = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5000/api/friendRequests/accept/${requestId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchReceivedRequests(); // Refresh received requests
    } catch (error) {
      console.error('Error accepting friend request:', error);
    }
  };

  const rejectFriendRequest = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5000/api/friendRequests/reject/${requestId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchReceivedRequests(); // Refresh received requests
    } catch (error) {
      console.error('Error rejecting friend request:', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    if (event.target.value.trim()) {
      setFilteredUsers(users.filter(user => 
        `${user.firstName} ${user.lastName}`.toLowerCase().includes(event.target.value.toLowerCase())
      ));
    } else {
      setFilteredUsers([]);
    }
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const toggleFriendsDropdown = () => {
    setFriendsDropdownOpen(!friendsDropdownOpen);
  };

  const handleSignOut = () => {
    dispatch(logout()); // Dispatch the logout action
    navigate('/signin'); // Redirect to the sign-in page
  };

  const sendFriendRequest = async (receiverId) => {
    try {
      await axios.post('http://localhost:5000/api/friendRequests/send', 
        { receiverId },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      // Optionally, update the UI to reflect the sent request
    } catch (error) {
      console.error('Error sending friend request:', error);
    }
  };

  const handlePostSubmit = async () => {
    if (post.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/api/posts/create', 
          {
            message: post,
            firstName: user?.firstName,
            lastName: user?.lastName,
            email: user?.email,
            picturePath: user?.picturePath
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts([...posts, response.data]);
        setPost('');
      } catch (error) {
        console.error('Error creating post:', error);
      }
    }
  };

  const handleLike = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/like/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleCommentSubmit = async (postId) => {
    if (comment.trim()) {
      try {
        const response = await axios.post(`http://localhost:5000/api/posts/comment/${postId}`, 
          {
            userId: user?._id,
            text: comment
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts(posts.map(post => post._id === postId ? response.data : post));
        setComment('');
      } catch (error) {
        console.error('Error commenting on post:', error);
      }
    }
  };

  const handleShare = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/share/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error sharing post:', error);
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
          {searchTerm && filteredUsers.length > 0 && (
            <div className="search-dropdown">
              {filteredUsers.map((user) => (
                <div key={user._id} className="search-result">
                  <img src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="search-image" />
                  <span>{user.firstName} {user.lastName}</span>
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} className="icon" onClick={handleIconClick} />
          <FontAwesomeIcon icon={faBell} className="icon" />
          <div className="dropdown">
            <FontAwesomeIcon icon={faUser} className="icon" onClick={toggleFriendsDropdown} />
            {friendsDropdownOpen && (
              <div className="dropdown-content">
                {receivedRequests.length > 0 ? (
                  receivedRequests.map((request) => (
                    <div key={request._id} className="dropdown-item">
                      <span>{request.sender.firstName} {request.sender.lastName}</span>
                      <button className="accept-button" onClick={() => acceptFriendRequest(request._id)}>Accept</button>
                      <button className="reject-button" onClick={() => rejectFriendRequest(request._id)}>Reject</button>
                    </div>
                  ))
                ) : (
                  <div className="dropdown-item">No friend requests</div>
                )}
              </div>
            )}
          </div>
          <div className="dropdown">
            <FontAwesomeIcon icon={faCog} className="icon" onClick={toggleDropdown} />
            {dropdownOpen && (
              <div className="dropdown-content">
                <div className="dropdown-item">Settings</div>
                <div className="dropdown-item" onClick={handleSignOut}>Sign Out</div>
              </div>
            )}
          </div>
        </div>
      </div>

      <div id="body-social">
        <div id="left-social">
          <h2>People you may know</h2>
          <div id="users-list">
            {users.map((user) => (
              <div key={user._id} className="user-item">
                <img src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="user-image" />
                <span>{user.firstName} {user.lastName}</span>
                <button
                  className="friend-request-button"
                  onClick={() => sendFriendRequest(user._id)}
                >
                  {sentRequests.includes(user._id) ? 'Request Sent' : <FontAwesomeIcon icon={faUserPlus} />}
                </button>
              </div>
            ))}
          </div>
        </div>

        <div id="center-social">
          <div id="post-container">
            <textarea
              placeholder="What's on your mind?"
              value={post}
              onChange={handlePostChange}
              id="post-input"
            />
            <div id="post-actions">
              <FontAwesomeIcon icon={faImage} className="post-action-icon" />
              <FontAwesomeIcon icon={faPaperclip} className="post-action-icon" />
              <FontAwesomeIcon icon={faFileAudio} className="post-action-icon" />
              <button onClick={handlePostSubmit} id="post-button">Post</button>
            </div>
          </div>

          <div id="posts-list">
            {posts.map(post => (
              <div key={post._id} className="post-item">
                <div className="post-header">
                  <img src={post.user?.picturePath ? `http://localhost:5000/${post.user.picturePath}` : '/default-profile.png'} alt="Profile" className="post-image" />
                  <div className="post-user">
                    <span className="post-user-name">{post.firstName} {post.lastName}</span>
                    <span className="post-time">{moment(post.createdAt).fromNow()}</span>
                  </div>
                </div>
                <div className="post-content">
                  <p>{post.message}</p>
                </div>
                <div className="post-actions">
                  <button onClick={() => handleLike(post._id)} className="post-action-button">
                    <FontAwesomeIcon icon={faThumbsUp} className="post-action-icon" /> Like {post.likes.length}
                  </button>
                  <button onClick={() => handleCommentSubmit(post._id)} className="post-action-button">
                    <FontAwesomeIcon icon={faComment} className="post-action-icon" /> Comment
                  </button>
                  <button onClick={() => handleShare(post._id)} className="post-action-button">
                    <FontAwesomeIcon icon={faShare} className="post-action-icon" /> Share {post.shares}
                  </button>
                </div>
                <div className="post-comments">
                  <input
                    type="text"
                    placeholder="Write a comment..."
                    value={comment}
                    onChange={handleCommentChange}
                    className="comment-input"
                  />
                </div>
              </div>
            ))}
          </div>
        </div>

        <div id="right-social">
          <div className="user-profile">
            <img src={user?.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="profile-image" />
            <span>{user?.firstName} {user?.lastName}</span>
            <div className="user-info">
              <FontAwesomeIcon icon={faMapMarkerAlt} className="info-icon" />
              <span>Location</span>
            </div>
            <div className="user-info">
              <FontAwesomeIcon icon={faBriefcase} className="info-icon" />
              <span>Job Title</span>
            </div>
            <div className="user-info">
              <FontAwesomeIcon icon={faTwitter} className="info-icon" />
              <span>Twitter</span>
            </div>
            <div className="user-info">
              <FontAwesomeIcon icon={faLinkedin} className="info-icon" />
              <span>LinkedIn</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Socialize;
