import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEnvelope, faBell, faCog, faUser, faMapMarkerAlt, faBriefcase, faImage, faPaperclip, faFileAudio, faUserPlus, faSignOutAlt, faChevronDown, faThumbsUp, faComment, faShare } from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import axios from 'axios';
import moment from 'moment';
import './Socialize.css';
import { logout } from '../actions/authActions'; // Adjust the path if necessary

const Socialize = () => {
  const [receivedRequests, setReceivedRequests] = useState([]);
  const [requestStatus, setRequestStatus] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [friendsDropdownOpen, setFriendsDropdownOpen] = useState(false);
  const [comment, setComment] = useState('');
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [sentRequests, setSentRequests] = useState([]);

  const { user } = useSelector(state => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  const handleIconClick = () => {
    navigate('/chat', { state: { currentUser: user } });
  };

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/posts/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        const filteredUsers = response.data
          .filter(u => u._id !== user._id)
          .slice(0, 5);
        setUsers(filteredUsers);
        setFilteredUsers(filteredUsers);
    
        const requestsResponse = await axios.get('http://localhost:5000/api/friendRequests/sent', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setSentRequests(requestsResponse.data.map(req => req.receiver.toString()));
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };
    
    fetchPosts();
    fetchUsers();
    fetchReceivedRequests();
  }, [user._id]);

  const fetchReceivedRequests = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/friendRequests/', {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
  
      if (Array.isArray(response.data.receivedRequests)) {
        setReceivedRequests(response.data.receivedRequests);
      } else {
        console.error('Received requests data is not an array:', response.data.receivedRequests);
        setReceivedRequests([]);
      }
  
      if (Array.isArray(response.data.sentRequests)) {
        setSentRequests(response.data.sentRequests.map(req => req.receiver.toString()));
      } else {
        console.error('Sent requests data is not an array:', response.data.sentRequests);
        setSentRequests([]);
      }
    } catch (error) {
      console.error('Error fetching friend requests:', error);
      setReceivedRequests([]);
      setSentRequests([]);
    }
  };

  const acceptFriendRequest = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5000/api/friendRequests/accept/${requestId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchReceivedRequests();
    } catch (error) {
      console.error('Error accepting friend request:', error);
    }
  };

  const rejectFriendRequest = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5000/api/friendRequests/reject/${requestId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchReceivedRequests();
    } catch (error) {
      console.error('Error rejecting friend request:', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    if (event.target.value.trim()) {
      setFilteredUsers(users.filter(user => 
        `${user.firstName} ${user.lastName}`.toLowerCase().includes(event.target.value.toLowerCase())
      ));
    } else {
      setFilteredUsers([]);
    }
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const toggleFriendsDropdown = () => {
    setFriendsDropdownOpen(!friendsDropdownOpen);
  };

  const handleSignOut = () => {
    dispatch(logout());
    navigate('/signin');
  };

  const sendFriendRequest = async (receiverId) => {
    try {
      await axios.post('http://localhost:5000/api/friendRequests/send', 
        { receiverId },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
    } catch (error) {
      console.error('Error sending friend request:', error);
    }
  };

  const handlePostSubmit = async () => {
    if (post.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/api/posts/create', 
          {
            message: post,
            firstName: user?.firstName,
            lastName: user?.lastName,
            email: user?.email,
            picturePath: user?.picturePath
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts([...posts, response.data]);
        setPost('');
      } catch (error) {
        console.error('Error creating post:', error);
      }
    }
  };

  const handleLike = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/like/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleCommentSubmit = async (postId) => {
    if (comment.trim()) {
      try {
        const response = await axios.post(`http://localhost:5000/api/posts/comment/${postId}`, 
          {
            userId: user?._id,
            text: comment
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts(posts.map(post => post._id === postId ? response.data : post));
        setComment('');
      } catch (error) {
        console.error('Error commenting on post:', error);
      }
    }
  };

  const handleShare = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/share/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error sharing post:', error);
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
          {searchTerm && filteredUsers.length > 0 && (
            <div className="search-dropdown">
              {filteredUsers.map((user) => (
                <div key={user._id} className="search-result">
                  <img src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="search-image" />
                  <span>{user.firstName} {user.lastName}</span>
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="icon-container">
          <FontAwesomeIcon
            icon={faEnvelope}
            className="top-icon"
            onClick={handleIconClick}
            style={{ cursor: 'pointer' }}
          />
          <FontAwesomeIcon icon={faBell} className="top-icon" />
          <FontAwesomeIcon icon={faCog} className="top-icon" />
          <div className="profile-container" onClick={toggleDropdown}>
            <img
              src={user?.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'}
              alt="Profile"
              className="profile-image"
            />
            <span>{user?.firstName} {user?.lastName}</span>
            <FontAwesomeIcon icon={faChevronDown} className="chevron-icon" />
            {dropdownOpen && (
              <div className="dropdown-menu">
                <div className="dropdown-item">
                  <FontAwesomeIcon icon={faUser} className="dropdown-icon" />
                  Profile
                </div>
                <div className="dropdown-item">
                  <FontAwesomeIcon icon={faCog} className="dropdown-icon" />
                  Settings
                </div>
                <div className="dropdown-item" onClick={handleSignOut}>
                  <FontAwesomeIcon icon={faSignOutAlt} className="dropdown-icon" />
                  Sign Out
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
      <div className="left-social">
        <div className="profile-header">
          <img
            src={user?.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'}
            alt="Profile"
            className="profile-image"
          />
          <h2>{user?.firstName} {user?.lastName}</h2>
          <div className="location">
            <FontAwesomeIcon icon={faMapMarkerAlt} className="location-icon" />
            <span>Islamabad, Pakistan</span>
          </div>
          <div className="job">
            <FontAwesomeIcon icon={faBriefcase} className="job-icon" />
            <span>Frontend Engineer</span>
          </div>
        </div>
        <div className="friend-requests">
          <h3>Friend Requests</h3>
          <FontAwesomeIcon
            icon={faUser}
            className="friend-icon"
            onClick={toggleFriendsDropdown}
          />
          {friendsDropdownOpen && (
            <div className="friend-requests-dropdown">
              {receivedRequests.length === 0 ? (
                <p>No friend requests</p>
              ) : (
                receivedRequests.map((request) => (
                  <div key={request._id} className="friend-request">
                    <img
                      src={request.sender.picturePath ? `http://localhost:5000/${request.sender.picturePath}` : '/default-profile.png'}
                      alt="Profile"
                      className="friend-request-image"
                    />
                    <span>{request.sender.firstName} {request.sender.lastName}</span>
                    <div className="friend-request-actions">
                      <button
                        className="accept-button"
                        onClick={() => acceptFriendRequest(request._id)}
                      >
                        Accept
                      </button>
                      <button
                        className="reject-button"
                        onClick={() => rejectFriendRequest(request._id)}
                      >
                        Reject
                      </button>
                    </div>
                  </div>
                ))
              )}
            </div>
          )}
        </div>
        <div className="social-media-icons">
          <FontAwesomeIcon icon={faTwitter} className="social-icon" />
          <FontAwesomeIcon icon={faLinkedin} className="social-icon" />
        </div>
      </div>
      <div className="middle-social">
        <div className="post-container">
          <textarea
            placeholder="What's on your mind?"
            value={post}
            onChange={handlePostChange}
          />
          <div className="post-actions">
            <FontAwesomeIcon icon={faImage} className="post-icon" />
            <FontAwesomeIcon icon={faPaperclip} className="post-icon" />
            <FontAwesomeIcon icon={faFileAudio} className="post-icon" />
            <button onClick={handlePostSubmit}>Post</button>
          </div>
        </div>
        {posts.map((post) => (
          <div key={post._id} className="post">
            <div className="post-header">
              <img
                src={post.picturePath ? `http://localhost:5000/${post.picturePath}` : '/default-profile.png'}
                alt="Profile"
                className="post-profile-image"
              />
              <div>
                <h3>{post.firstName} {post.lastName}</h3>
                <p>{moment(post.createdAt).fromNow()}</p>
              </div>
            </div>
            <p className="post-message">{post.message}</p>
            <div className="post-actions">
              <FontAwesomeIcon
                icon={faThumbsUp}
                className="post-action-icon"
                onClick={() => handleLike(post._id)}
              />
              <span>{post.likes.length}</span>
              <FontAwesomeIcon
                icon={faComment}
                className="post-action-icon"
                onClick={() => handleCommentSubmit(post._id)}
              />
              <span>{post.comments.length}</span>
              <FontAwesomeIcon
                icon={faShare}
                className="post-action-icon"
                onClick={() => handleShare(post._id)}
              />
              <span>{post.shares.length}</span>
            </div>
            <div className="comments-section">
              {post.comments.map((comment) => (
                <div key={comment._id} className="comment">
                  <p>{comment.text}</p>
                </div>
              ))}
              <input
                type="text"
                placeholder="Add a comment..."
                value={comment}
                onChange={handleCommentChange}
              />
              <button onClick={() => handleCommentSubmit(post._id)}>Comment</button>
            </div>
          </div>
        ))}
      </div>
      <div className="right-social">
        <h3>People You May Know</h3>
        {users.map((user) => (
          <div key={user._id} className="user-card">
            <img
              src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'}
              alt="Profile"
              className="user-image"
            />
            <div className="user-info">
              <span>{user.firstName} {user.lastName}</span>
              <FontAwesomeIcon
                icon={sentRequests.includes(user._id) ? 'faUserPlus' : 'faUserPlus'}
                className="add-user-icon"
                onClick={() => sendFriendRequest(user._id)}
                style={{ cursor: 'pointer', color: sentRequests.includes(user._id) ? 'green' : 'black' }}
              />
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Socialize;
