import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEnvelope, faBell, faCog, faUser, faMapMarkerAlt, faBriefcase, faImage, faPaperclip, faFileAudio, faUserPlus, faSignOutAlt, faChevronDown, faThumbsUp, faComment, faShare } from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import axios from 'axios';
import moment from 'moment';
import './Socialize.css';
import { logout } from '../actions/authActions'; // Adjust the path if necessary

const Socialize = () => {
  const [receivedRequests, setReceivedRequests] = useState([]);
  const [requestStatus, setRequestStatus] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [friendsDropdownOpen, setFriendsDropdownOpen] = useState(false);
  const [comment, setComment] = useState('');
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [sentRequests, setSentRequests] = useState([]);

  const { user } = useSelector(state => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate(); // Hook for navigation

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/posts/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        const filteredUsers = response.data
          .filter(u => u._id !== user._id)
          .slice(0, 5);
        setUsers(filteredUsers);
        setFilteredUsers(filteredUsers);
    
        // Fetch sent friend requests
        const requestsResponse = await axios.get('http://localhost:5000/api/friendRequests/sent', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setSentRequests(requestsResponse.data.map(req => req.receiver.toString()));
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    const fetchReceivedRequests = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/friendRequests/', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setReceivedRequests(response.data.receivedRequests); // Adjust according to API response
      } catch (error) {
        console.error('Error fetching received requests:', error);
      }
    };

    fetchPosts();
    fetchUsers();
    fetchReceivedRequests(); // Ensure this is called when component mounts
  }, [user._id]);

  const acceptFriendRequest = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5000/api/friendRequests/accept/${requestId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchReceivedRequests(); // Refresh received requests
    } catch (error) {
      console.error('Error accepting friend request:', error);
    }
  };

  const rejectFriendRequest = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5000/api/friendRequests/reject/${requestId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchReceivedRequests(); // Refresh received requests
    } catch (error) {
      console.error('Error rejecting friend request:', error);
    }
  };

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    if (event.target.value.trim()) {
      setFilteredUsers(users.filter(user =>
        `${user.firstName} ${user.lastName}`.toLowerCase().includes(event.target.value.toLowerCase())
      ));
    } else {
      setFilteredUsers([]);
    }
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleSignOut = () => {
    dispatch(logout()); // Dispatch the logout action
    navigate('/signin'); // Redirect to the sign-in page
  };

  const sendFriendRequest = async (receiverId) => {
    try {
      await axios.post('http://localhost:5000/api/friendRequests/send',
        { receiverId },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      // Optionally, update the UI to reflect the sent request
    } catch (error) {
      console.error('Error sending friend request:', error);
    }
  };

  const handlePostSubmit = async () => {
    if (post.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/api/posts/create',
          {
            message: post,
            firstName: user?.firstName,
            lastName: user?.lastName,
            email: user?.email,
            picturePath: user?.picturePath
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts([...posts, response.data]);
        setPost('');
      } catch (error) {
        console.error('Error creating post:', error);
      }
    }
  };

  const handleLike = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/like/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleCommentSubmit = async (postId) => {
    if (comment.trim()) {
      try {
        const response = await axios.post(`http://localhost:5000/api/posts/comment/${postId}`,
          {
            userId: user?._id,
            text: comment
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts(posts.map(post => post._id === postId ? response.data : post));
        setComment('');
      } catch (error) {
        console.error('Error commenting on post:', error);
      }
    }
  };

  const handleShare = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/share/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error sharing post:', error);
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
          {searchTerm && filteredUsers.length > 0 && (
            <div className="search-dropdown">
              {filteredUsers.map((user) => (
                <div key={user._id} className="search-result">
                  <img src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="search-image" />
                  <span>{user.firstName} {user.lastName}</span>
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} className="top-icon" />
          <FontAwesomeIcon icon={faBell} className="top-icon" />
          <FontAwesomeIcon icon={faCog} className="top-icon" />
          <FontAwesomeIcon icon={faUser} className="top-icon" onClick={toggleDropdown} />
          {dropdownOpen && (
            <div className="dropdown-menu">
              <FontAwesomeIcon icon={faSignOutAlt} className="dropdown-item" onClick={handleSignOut} />
              <span className="dropdown-item">Sign Out</span>
            </div>
          )}
        </div>
      </div>
      <div id="left-social">
        <div className="friends-request-dropdown">
          <FontAwesomeIcon icon={faUserPlus} onClick={() => setFriendsDropdownOpen(!friendsDropdownOpen)} />
          {friendsDropdownOpen && (
            <div className="dropdown-menu">
              <h3>Friend Requests</h3>
              {receivedRequests.length > 0 ? (
                receivedRequests.map((request) => (
                  <div key={request._id} className="friend-request">
                    <img src={request.sender.picturePath ? `http://localhost:5000/${request.sender.picturePath}` : '/default-profile.png'} alt="Profile" className="request-image" />
                    <span>{request.sender.firstName} {request.sender.lastName}</span>
                    <button onClick={() => acceptFriendRequest(request._id)}>Accept</button>
                    <button onClick={() => rejectFriendRequest(request._id)}>Reject</button>
                  </div>
                ))
              ) : (
                <p>No friend requests</p>
              )}
            </div>
          )}
        </div>
        <div className="profile-info">
          <img src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="profile-image" />
          <h2>{user.firstName} {user.lastName}</h2>
        </div>
      </div>
      <div id="right-social">
        <textarea
          placeholder="What's on your mind?"
          value={post}
          onChange={handlePostChange}
        />
        <button onClick={handlePostSubmit}>Post</button>
        {posts.map((post) => (
          <div key={post._id} className="post">
            <div className="post-header">
              <img src={post.picturePath ? `http://localhost:5000/${post.picturePath}` : '/default-profile.png'} alt="Profile" className="post-image" />
              <span>{post.firstName} {post.lastName}</span>
              <span>{moment(post.createdAt).fromNow()}</span>
            </div>
            <p>{post.message}</p>
            <div className="post-actions">
              <button onClick={() => handleLike(post._id)}>
                <FontAwesomeIcon icon={faThumbsUp} /> {post.likes.length}
              </button>
              <button onClick={() => handleCommentSubmit(post._id)}>
                <FontAwesomeIcon icon={faComment} /> {post.comments.length}
              </button>
              <button onClick={() => handleShare(post._id)}>
                <FontAwesomeIcon icon={faShare} /> Share
              </button>
            </div>
            <div className="post-comments">
              {post.comments.map((comment) => (
                <div key={comment._id} className="comment">
                  <img src={comment.user.picturePath ? `http://localhost:5000/${comment.user.picturePath}` : '/default-profile.png'} alt="Profile" className="comment-image" />
                  <span>{comment.user.firstName} {comment.user.lastName}</span>
                  <p>{comment.text}</p>
                </div>
              ))}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Socialize;
