import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Chat.css';
import { useLocation } from 'react-router-dom';

const Chat = () => {
  const [message, setMessage] = useState('');
  const [users, setUsers] = useState([]);
  const [selectedUser, setSelectedUser] = useState(null);
  const [chatData, setChatData] = useState([]);
  const location = useLocation();
  const currentUser = location.state?.currentUser; // Access the passed user data

  useEffect(() => {
    // Fetch users from the backend
    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/all'); // Adjust the API endpoint as needed
        // Exclude the current user from the users list
        const filteredUsers = response.data.filter(user => user._id !== currentUser._id);
        setUsers(filteredUsers);
      } catch (err) {
        console.error('Error fetching users:', err);
      }
    };

    fetchUsers();
  }, [currentUser]);

  useEffect(() => {
    // Fetch chat data when a user is selected
    if (selectedUser) {
      const fetchChatData = async () => {
        try {
          // Replace this with the actual endpoint for fetching chat messages
          const response = await axios.get(`http://localhost:5000/api/chats/${selectedUser._id}`);
          setChatData(response.data);
        } catch (err) {
          console.error('Error fetching chat data:', err);
        }
      };

      fetchChatData();
    }
  }, [selectedUser]);

  const handleUserClick = (user) => {
    setSelectedUser(user);
  };

  return (
    <div className="chat-container">
      <div className="sidebar">
        <div className="search-container">
          <input type="text" placeholder="Search" className="search-input" />
          <span className="search-icon">üîç</span>
        </div>
        <div className="chats-section">
          <h2>You</h2>
          {currentUser && (
            <div className="chat-item">
              <img src={currentUser.picturePath ? `http://localhost:5000/${currentUser.picturePath}` : '/default-profile.png'} alt="Profile" className="add-image" />
              <span>{currentUser.firstName} {currentUser.lastName}</span>
            </div>
          )}
          <h2>Chats</h2>
          {users.map((user) => (
            <div
              key={user._id}
              className={`chat-item ${selectedUser?._id === user._id ? 'selected' : ''}`}
              onClick={() => handleUserClick(user)}
            >
              <img src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="add-image" />
              <span>{user.firstName} {user.lastName}</span>
            </div>
          ))}
        </div>
      </div>
      <div className="chat-area">
        <div className="chat-header">
        <img src={selectedUser.picturePath ? `http://localhost:5000/${selectedUser.picturePath}` : '/default-profile.png'} alt="Profile" className="image" />
          <h1>{selectedUser ? ` ${selectedUser.firstName} ${selectedUser.lastName}` : 'Select a user to chat'}</h1>
          <div className="header-icons">
            <span className="icon">üåô</span>
            <span className="icon">‚öôÔ∏è</span>
          </div>
        </div>
        <div className="messages">
          {chatData.map((chat) => (
            <div key={chat.id} className={`message ${chat.sender === currentUser.firstName ? 'sent' : 'received'}`}>
              <div className="message-content">
                <p>{chat.message}</p>
                <span className="message-time">{chat.time}</span>
              </div>
            </div>
          ))}
        </div>
        <div className="input-area">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            placeholder="Write a message..."
            className="message-input"
          />
          <button className="send-button">
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default Chat;
