import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { 
  faSearch, faEnvelope, faBell, faCog, faMapMarkerAlt, faBriefcase, 
  faImage, faPaperclip, faFileAudio, faUserPlus, faSignOutAlt, 
  faChevronDown, faThumbsUp, faComment, faShare 
} from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import axios from 'axios';
import moment from 'moment';
import './Socialize.css';
import { logout } from '../actions/authActions'; // Adjust the path if necessary

const Socialize = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [comment, setComment] = useState('');
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);

  const { user } = useSelector(state => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate();

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/posts/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        const filteredUsers = response.data.filter(u => u._id !== user._id).slice(0, 5);
        setUsers(filteredUsers);
        setFilteredUsers(filteredUsers);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchPosts();
    fetchUsers();
  }, [user._id]);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    if (event.target.value.trim()) {
      setFilteredUsers(users.filter(user => 
        `${user.firstName} ${user.lastName}`.toLowerCase().includes(event.target.value.toLowerCase())
      ));
    } else {
      setFilteredUsers([]);
    }
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleSignOut = () => {
    dispatch(logout());
    navigate('/signin');
  };

  const handlePostSubmit = async () => {
    if (post.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/api/posts/create', 
          {
            message: post,
            firstName: user?.firstName,
            lastName: user?.lastName,
            email: user?.email,
            picturePath: user?.picturePath
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts([...posts, response.data]);
        setPost('');
      } catch (error) {
        console.error('Error creating post:', error);
      }
    }
  };

  const handleLike = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/like/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleCommentSubmit = async (postId) => {
    if (comment.trim()) {
      try {
        const response = await axios.post(`http://localhost:5000/api/posts/comment/${postId}`, 
          {
            userId: user?._id,
            text: comment
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts(posts.map(post => post._id === postId ? response.data : post));
        setComment('');
      } catch (error) {
        console.error('Error commenting on post:', error);
      }
    }
  };

  const handleShare = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/share/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error sharing post:', error);
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
          {searchTerm && filteredUsers.length > 0 && (
            <div className="search-dropdown">
              {filteredUsers.map((user) => (
                <div key={user._id} className="search-result">
                  <img src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="search-image" />
                  <span>{user.firstName} {user.lastName}</span>
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} className="top-icon" />
          <FontAwesomeIcon icon={faBell} className="top-icon" />
          <FontAwesomeIcon icon={faCog} className="top-icon" />
          <div className="user-dropdown">
            <span className="user-name" onClick={toggleDropdown}>
              {user?.firstName}
              <FontAwesomeIcon icon={faChevronDown} className="dropdown-icon" />
            </span>
            {dropdownOpen && (
              <div className="dropdown-menu">
                <button onClick={handleSignOut} className="signout-button">
                  <FontAwesomeIcon icon={faSignOutAlt} /> Sign Out
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
      <div id="mid-social">
        <div className="profile-card">
          <img src={user?.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="profile-image" />
          <h2>{user?.firstName} {user?.lastName}</h2>
          <p>{user?.friends?.length} friends</p>
          <div className="location">
            <FontAwesomeIcon icon={faMapMarkerAlt} /> {user?.location}
          </div>
          <div className="job-title">
            <FontAwesomeIcon icon={faBriefcase} /> {user?.occupation}
          </div>
          <div className="social-profiles">
            <div className="social-profile">
              <FontAwesomeIcon icon={faTwitter} /> Twitter
            </div>
            <div className="social-profile">
              <FontAwesomeIcon icon={faLinkedin} /> LinkedIn
            </div>
          </div>
        </div>
        <div className="post">
          <div className="postMessage">
            <div className="message">
              <img src={user?.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="post-image" />
              <input
                type="text"
                placeholder="What's on your mind..."
                value={post}
                onChange={handlePostChange}
                id="add-post"
              />
            </div>
            <div className="post-icons">
              <div className="icon-container">
                <FontAwesomeIcon icon={faImage} className="post-icon" />
                <FontAwesomeIcon icon={faPaperclip} className="post-icon" />
                <FontAwesomeIcon icon={faFileAudio} className="post-icon" />
              </div>
              <button className="post-button" onClick={handlePostSubmit}>Post</button>
            </div>
          </div>
          <div className="posts-list">
            {posts.map((post, index) => (
              <div key={index} className="posted">
                <div className="user-info">
                  <img src={post.picturePath ? `http://localhost:5000/${post.picturePath}` : '/default-profile.png'} alt="Profile" className="post-image" />
                  <div className="user-details">
                    <span className="user-name">{post.firstName} {post.lastName}</span>
                    <span className="post-time">{moment(post.createdAt).fromNow()}</span>
                  </div>
                </div>
                <div className="post-text">
                  <p>{post.message}</p>
                </div>
                <div className="post-actions">
                  <FontAwesomeIcon icon={faThumbsUp} className="post-action" onClick={() => handleLike(post._id)} />
                  <span>{post.likes.length}</span>
                  <FontAwesomeIcon icon={faComment} className="post-action" onClick={() => document.getElementById(`comment-${post._id}`).focus()} />
                  <span>{post.comments.length}</span>
                  <FontAwesomeIcon icon={faShare} className="post-action" onClick={() => handleShare(post._id)} />
                </div>
                <div className="comment-section">
                  {post.comments.map((comment, index) => (
                    <div key={index} className="comment">
                      <span>{comment.userId}</span>
                      <span>{comment.text}</span>
                    </div>
                  ))}
                  <div className="add-comment">
                    <input
                      type="text"
                      id={`comment-${post._id}`}
                      placeholder="Add a comment..."
                      value={comment}
                      onChange={handleCommentChange}
                    />
                    <button className="comment-button" onClick={() => handleCommentSubmit(post._id)}>Comment</button>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
        <div className="suggestions">
          <h2>Suggested Profiles</h2>
          {users.map((user) => (
            <div key={user._id} className="suggested-profile">
              <img src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="suggested-profile-image" />
              <div className="profile-info">
                <span>{user.firstName} {user.lastName}</span>
                <button className="add-friend-button">
                  <FontAwesomeIcon icon={faUserPlus} /> Add Friend
                </button>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div id="bottom-social"></div>
    </div>
  );
};

export default Socialize;
