import React from 'react';
import { Box, Text } from '@chakra-ui/react';

const NotificationBadge = ({ count }) => (
  <Box
    position="relative"
    display="inline-block"
  >
    <Text
      bg="red.500"
      color="white"
      borderRadius="full"
      px={2}
      py={1}
      fontSize="sm"
      fontWeight="bold"
      position="absolute"
      top="-10px"
      right="-10px"
    >
      {count}
    </Text>
  </Box>
);

export default NotificationBadge;
