import React from 'react';
import { FaRobot, FaSmile, FaPaperPlane } from 'react-icons/fa';
import './Chat.css';

const users = [
  { name: 'Robin Sparkles', icon: <FaRobot /> },
  { name: 'Cook Pu', icon: <FaRobot /> },
  { name: 'Princess Consuela Banana Hammock', icon: <FaRobot /> },
  { name: 'Burrito Supreme', icon: <FaRobot /> },
  { name: 'The Commodore', icon: <FaRobot /> },
  { name: 'Lorenzo von Matterhorn', icon: <FaRobot /> },
];

const messages = [
  { text: 'Hi TedðŸ˜Š', time: '52 minutes ago', sender: 'Robin Sparkles' },
  { text: 'heyðŸ‘‹', time: '36 minutes ago', sender: 'Robin Sparkles' },
  { text: "Let's go to the mall", time: '44 minutes ago', sender: 'You' },
];

const Chat = () => {
  return (
    <div className="app">
      <div className="sidebar">
        <div className="chat-list">
          <input type="text" placeholder="Search" className="search-bar" />
          <div className="chat-items">
            {users.map((user, index) => (
              <div key={index} className="chat-item">
                {user.icon}
                <span>{user.name}</span>
              </div>
            ))}
          </div>
        </div>
      </div>
      <div className="main">
        <div className="chat-window">
          <div className="chat-header">
            <div className="chat-header-user">
              <FaRobot />
              <span>Robin Sparkles</span>
            </div>
          </div>
          <div className="chat-messages">
            {messages.map((message, index) => (
              <div key={index} className={`chat-message ${message.sender === 'You' ? 'sent' : 'received'}`}>
                <span>{message.text}</span>
                <span className="chat-time">{message.time}</span>
              </div>
            ))}
          </div>
          <div className="chat-input">
            <FaSmile />
            <input type="text" placeholder="Write a message" />
            <FaPaperPlane />
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chat;
