import React, { useState, useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faCheck,faSearch, faEnvelope, faBell, faCog, faUser, faMapMarkerAlt, faBriefcase, faImage, faPaperclip, faFileAudio, faUserPlus, faSignOutAlt, faChevronDown, faThumbsUp, faComment, faShare } from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import axios from 'axios';
import moment from 'moment';
import './Socialize.css';
import { logout } from '../actions/authActions'; // Adjust the path if necessary

const Socialize = () => {
  const [receivedRequests, setReceivedRequests] = useState([]);
  const [requestStatus, setRequestStatus] = useState({});
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [friendsDropdownOpen, setFriendsDropdown] = useState(false);
  const [comment, setComment] = useState('');
  const [users, setUsers] = useState([]);
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [sentRequests, setSentRequests] = useState([]);

  const { user } = useSelector(state => state.auth);
  const dispatch = useDispatch();
  const navigate = useNavigate(); // Hook for navigation

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/posts/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        const filteredUsers = response.data
          .filter(u => u._id !== user._id)
          .slice(0, 5);
        setUsers(filteredUsers);
        setFilteredUsers(filteredUsers);
    
        // Fetch sent friend requests
        const requestsResponse = await axios.get('http://localhost:5000/api/friendRequests/sent', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setSentRequests(requestsResponse.data.map(req => req.receiver.toString()));
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };
    
    fetchPosts();
    fetchUsers();
    fetchReceivedRequests();
  }, [user._id]);

  const fetchReceivedRequests = async () => {
    try {
      const response = await axios.get('http://localhost:5000/api/friendRequests/', {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
  
      // Ensure that receivedRequests and sentRequests are always arrays
      if (Array.isArray(response.data.receivedRequests)) {
        setReceivedRequests(response.data.receivedRequests);
      } else {
        console.error('Received requests data is not an array:', response.data.receivedRequests);
        setReceivedRequests([]);
      }
  
      if (Array.isArray(response.data.sentRequests)) {
        setSentRequests(response.data.sentRequests.map(req => req.receiver.toString()));
      } else {
        console.error('Sent requests data is not an array:', response.data.sentRequests);
        setSentRequests([]);
      }
    } catch (error) {
      console.error('Error fetching friend requests:', error);
      setReceivedRequests([]);
      setSentRequests([]);
    }
  };
  

  const acceptFriendRequest = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5000/api/friendRequests/accept/${requestId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchReceivedRequests(); // Refresh received requests
    } catch (error) {
      console.error('Error accepting friend request:', error);
    }
  };
  
  const rejectFriendRequest = async (requestId) => {
    try {
      await axios.patch(`http://localhost:5000/api/friendRequests/reject/${requestId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      fetchReceivedRequests(); // Refresh received requests
    } catch (error) {
      console.error('Error rejecting friend request:', error);
    }
  };
  

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
    if (event.target.value.trim()) {
      setFilteredUsers(users.filter(user => 
        `${user.firstName} ${user.lastName}`.toLowerCase().includes(event.target.value.toLowerCase())
      ));
    } else {
      setFilteredUsers([]);
    }
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };
  const Dropdown = () => {
    setFriendsDropdown(!friendsDropdownOpen);
  };

  const handleSignOut = () => {
    dispatch(logout()); // Dispatch the logout action
    navigate('/signin'); // Redirect to the sign-in page
  };
  
  const sendFriendRequest = async (receiverId) => {
    try {
      await axios.post('http://localhost:5000/api/friendRequests/send', 
        { receiverId },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      setSentRequests([...sentRequests, receiverId]);
    } catch (error) {
      console.error('Error sending friend request:', error);
    }
  };
  
  const handlePostSubmit = async () => {
    if (post.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/api/posts/create', 
          {
            message: post,
            firstName: user?.firstName,
            lastName: user?.lastName,
            email: user?.email,
            picturePath: user?.picturePath
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts([...posts, response.data]);
        setPost('');
      } catch (error) {
        console.error('Error creating post:', error);
      }
    }
  };

  const handleLike = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/like/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleCommentSubmit = async (postId) => {
    if (comment.trim()) {
      try {
        const response = await axios.post(`http://localhost:5000/api/posts/comment/${postId}`, 
          {
            userId: user?._id,
            text: comment
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts(posts.map(post => post._id === postId ? response.data : post));
        setComment('');
      } catch (error) {
        console.error('Error commenting on post:', error);
      }
    }
  };

  const handleShare = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/share/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error sharing post:', error);
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-box">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
          />
          <FontAwesomeIcon icon={faSearch} />
          {searchTerm && (
            <div className="search-results">
              {filteredUsers.length > 0 ? (
                filteredUsers.map(user => (
                  <div key={user._id} className="search-result">
                    <img src={`http://localhost:5000/uploads/${user.picturePath}`} alt="Profile" />
                    <span>{user.firstName} {user.lastName}</span>
                  </div>
                ))
              ) : (
                <p>No results found</p>
              )}
            </div>
          )}
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} />
          <div className="friend-request-dropdown">
            <FontAwesomeIcon icon={faUser} onClick={Dropdown} />
            {friendsDropdownOpen && (
              <div className="friend-request-menu">
                {receivedRequests.length > 0 ? (
                  receivedRequests.map(request => (
                    <div key={request._id} className="friend-request-item">
                      <img src={`http://localhost:5000/uploads/${request.sender.picturePath}`} alt="Profile" />
                      <span>{request.sender.firstName} {request.sender.lastName}</span>
                      <button onClick={() => acceptFriendRequest(request._id)}>Accept</button>
                      <button onClick={() => rejectFriendRequest(request._id)}>Reject</button>
                    </div>
                  ))
                ) : (
                  <p>No friend requests</p>
                )}
              </div>
            )}
          </div>
          <FontAwesomeIcon icon={faBell} onClick={toggleDropdown} />
          {dropdownOpen && (
            <div className="notification-dropdown">
              <p>Notifications go here</p>
            </div>
          )}
          <FontAwesomeIcon icon={faCog} />
          <FontAwesomeIcon icon={faSignOutAlt} onClick={handleSignOut} />
        </div>
      </div>
      <div id="profile-social">
        <div className="profile-pic">
          <img src={`http://localhost:5000/uploads/${user?.picturePath}`} alt="Profile" />
        </div>
        <div className="profile-info">
          <h2>{user?.firstName} {user?.lastName}</h2>
          <p><FontAwesomeIcon icon={faMapMarkerAlt} /> Islamabad, Pakistan</p>
          <p><FontAwesomeIcon icon={faBriefcase} /> Frontend Engineer</p>
          <div className="social-icons">
            <FontAwesomeIcon icon={faTwitter} />
            <FontAwesomeIcon icon={faLinkedin} />
          </div>
        </div>
      </div>
      <div id="mid-social">
        <div className="mid-top">
          <input
            type="text"
            placeholder="What's on your mind?"
            value={post}
            onChange={handlePostChange}
          />
          <div className="mid-icons">
            <FontAwesomeIcon icon={faImage} />
            <FontAwesomeIcon icon={faPaperclip} />
            <FontAwesomeIcon icon={faFileAudio} />
          </div>
          <button onClick={handlePostSubmit}>Post</button>
        </div>
        <div className="posts">
          {posts.map(post => (
            <div key={post._id} className="post">
              <div className="post-header">
                <img src={`http://localhost:5000/uploads/${post.picturePath}`} alt="Profile" />
                <div className="post-info">
                  <h3>{post.firstName} {post.lastName}</h3>
                  <p>{moment(post.createdAt).fromNow()}</p>
                </div>
              </div>
              <div className="post-content">
                <p>{post.message}</p>
              </div>
              <div className="post-actions">
                <div onClick={() => handleLike(post._id)}>
                  <FontAwesomeIcon icon={faThumbsUp} />
                  <span>{post.likes.length}</span>
                </div>
                <div>
                  <FontAwesomeIcon icon={faComment} />
                  <span>{post.comments.length}</span>
                </div>
                <div onClick={() => handleShare(post._id)}>
                  <FontAwesomeIcon icon={faShare} />
                  <span>{post.shares}</span>
                </div>
              </div>
              <div className="post-comments">
                {post.comments.map(comment => (
                  <div key={comment._id} className="comment">
                    <p><strong>{comment.userId.firstName} {comment.userId.lastName}:</strong> {comment.text}</p>
                  </div>
                ))}
                <div className="add-comment">
                  <input
                    type="text"
                    placeholder="Add a comment..."
                    value={comment}
                    onChange={handleCommentChange}
                  />
                  <button onClick={() => handleCommentSubmit(post._id)}>Comment</button>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
      <div id="right-social">
        <h2>People you may know</h2>
        <ul>
          {users.map(user => (
            <li key={user._id}>
              <div className="user-card">
                <img src={`http://localhost:5000/uploads/${user.picturePath}`} alt="Profile" />
                <div>
                  <h3>{user.firstName} {user.lastName}</h3>
                  <button onClick={() => sendFriendRequest(user._id)}>
                    <FontAwesomeIcon
                      icon={sentRequests.includes(user._id) ? faCheck : faUserPlus}
                    />
                  </button>
                </div>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default Socialize;
