import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { signup } from '../actions/authActions';
import styles from './Signup.module.css';

const Signup = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    location: '',
    occupation: '',
    picturePath: '',
  });

  const dispatch = useDispatch();
  const { error } = useSelector((state) => state.auth);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    dispatch(signup(formData));
  };

  return (
    <div className={styles.signupContainer}>
      <h1 className={styles.header}>Sociopedia</h1>
      <h2 className={styles.subtitle}>Welcome to Sociopedia, the Social Media For Sociopaths!</h2>
      {error && <p className={styles.error}>{error}</p>}
      <form onSubmit={handleSubmit} className={styles.form}>
        <div className={styles.row}>
          <input
            type="text"
            placeholder="First Name"
            name="firstName"
            value={formData.firstName}
            onChange={handleChange}
            className={styles.input}
            required
          />
          <input
            type="text"
            placeholder="Last Name"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            className={styles.input}
            required
          />
        </div>
        <input
          type="text"
          placeholder="Location"
          name="location"
          value={formData.location}
          onChange={handleChange}
          className={styles.input}
        />
        <input
          type="text"
          placeholder="Occupation"
          name="occupation"
          value={formData.occupation}
          onChange={handleChange}
          className={styles.input}
        />
        <input
          type="text"
          placeholder="Add Picture Here"
          name="picturePath"
          value={formData.picturePath}
          onChange={handleChange}
          className={styles.input}
        />
        <input
          type="email"
          placeholder="Email"
          name="email"
          value={formData.email}
          onChange={handleChange}
          className={styles.input}
          required
        />
        <input
          type="password"
          placeholder="Password"
          name="password"
          value={formData.password}
          onChange={handleChange}
          className={styles.input}
          required
        />
        <button type="submit" className={styles.button}>REGISTER</button>
      </form>
      <p className={styles.loginText}>Already have an account? <a href="/login" className={styles.loginLink}>Login here.</a></p>
    </div>
  );
};

export default Signup;
