import React, { useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { signup } from '../actions/authActions';
import './Signup.css';

const Signup = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    location: '',
    occupation: '',
    picturePath: '',
  });

  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { error, isAuthenticated } = useSelector((state) => state.auth);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, picturePath: e.target.files[0] });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const formDataToSubmit = new FormData();
    for (const key in formData) {
      formDataToSubmit.append(key, formData[key]);
    }
    dispatch(signup(formDataToSubmit));
  };

  React.useEffect(() => {
    if (isAuthenticated) {
      navigate('/signin');
    }
  }, [isAuthenticated, navigate]);

  return (
    <div id="outerContainer">
      <div id="signupContainer">
        <h1 id="header">Sociopedia</h1>
        <h2 id="subtitle">Welcome to Sociopedia, the Social Media For Sociopaths!</h2>
        {error && <p id="error">{error}</p>}
        <form onSubmit={handleSubmit} id="form" encType="multipart/form-data">
          <div id="row">
            <input
              type="text"
              placeholder="First Name"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              id="input"
              required
            />
            <input
              type="text"
              placeholder="Last Name"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              id="input"
              required
            />
          </div>
          <input
            type="text"
            placeholder="Location"
            name="location"
            value={formData.location}
            onChange={handleChange}
            id="input"
          />
          <input
            type="text"
            placeholder="Occupation"
            name="occupation"
            value={formData.occupation}
            onChange={handleChange}
            id="input"
          />
          <input
            type="file"
            placeholder="Add Picture Here"
            name="picturePath"
            onChange={handleFileChange}
            id="input"
          />
          <input
            type="email"
            placeholder="Email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            id="input"
            required
          />
          <input
            type="password"
            placeholder="Password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            id="input"
            required
          />
          <button type="submit" id="button">REGISTER</button>
        </form>
        <p id="loginText">Already have an account? <a href="/signin" id="loginLink">Login here.</a></p>
      </div>
    </div>
  );
};

export default Signup;
