import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEnvelope, faBell, faCog, faMapMarkerAlt, faBriefcase, faImage, faPaperclip, faFileAudio, faUserPlus, faSignOutAlt, faChevronDown, faThumbsUp, faComment, faShare } from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import axios from 'axios';
import moment from 'moment';
import './Socialize.css';

const Socialize = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [comment, setComment] = useState('');

  const { user } = useSelector(state => state.auth);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/posts/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, []);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleSignOut = () => {
    localStorage.removeItem('token');
    window.location.reload();
  };

  const handlePostSubmit = async () => {
    if (post.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/api/posts/create', 
          {
            message: post,
            firstName: user?.firstName,
            lastName: user?.lastName,
            email: user?.email,
            picturePath: user?.picturePath
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts([...posts, response.data]);
        setPost('');
      } catch (error) {
        console.error('Error creating post:', error);
      }
    }
  };

  const handleLike = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/like/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleCommentSubmit = async (postId) => {
    if (comment.trim()) {
      try {
        const response = await axios.post(`http://localhost:5000/api/posts/comment/${postId}`, 
          {
            text: comment
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts(posts.map(post => post._id === postId ? response.data : post));
        setComment('');
      } catch (error) {
        console.error('Error commenting on post:', error);
      }
    }
  };

  const handleShare = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/share/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error sharing post:', error);
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} className="top-icon" />
          <FontAwesomeIcon icon={faBell} className="top-icon" />
          <FontAwesomeIcon icon={faCog} className="top-icon" />
          <div className="profile-dropdown">
            <img
              src={`http://localhost:5000/images/${user.picturePath}`}
              alt="Profile"
              className="profile-image"
              onClick={toggleDropdown}
            />
            {dropdownOpen && (
              <div className="dropdown-content">
                <div className="profile-info">
                  <p><strong>{user.firstName} {user.lastName}</strong></p>
                  <p><FontAwesomeIcon icon={faMapMarkerAlt} className="profile-icon" /> Islamabad, Pakistan</p>
                  <p><FontAwesomeIcon icon={faBriefcase} className="profile-icon" /> Frontend Engineer</p>
                </div>
                <hr />
                <div className="logout-section">
                  <p onClick={handleSignOut}><FontAwesomeIcon icon={faSignOutAlt} className="logout-icon" /> Logout</p>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      <div id="main-social">
        <div id="left-social">
          <div className="icon-bar">
            <FontAwesomeIcon icon={faTwitter} className="sidebar-icon" />
            <FontAwesomeIcon icon={faLinkedin} className="sidebar-icon" />
            <FontAwesomeIcon icon={faUserPlus} className="sidebar-icon" />
          </div>
        </div>

        <div id="middle-social">
          <div className="post-container">
            <div className="post-input">
              <textarea
                placeholder="Write your post here..."
                value={post}
                onChange={handlePostChange}
                id="post-textarea"
              />
              <div className="post-actions">
                <FontAwesomeIcon icon={faImage} className="post-action-icon" />
                <FontAwesomeIcon icon={faPaperclip} className="post-action-icon" />
                <FontAwesomeIcon icon={faFileAudio} className="post-action-icon" />
              </div>
              <button onClick={handlePostSubmit} id="post-button">Post</button>
            </div>

            <div className="feed-container">
              {posts.map((post) => (
                <div key={post._id} className="post">
                  <div className="post-header">
                    <img
                      src={`http://localhost:5000/images/${post.picturePath}`}
                      alt="Profile"
                      className="post-profile-image"
                    />
                    <div className="post-user-info">
                      <h4>{post.firstName} {post.lastName}</h4>
                      <p>{moment(post.date).fromNow()}</p>
                    </div>
                  </div>
                  <div className="post-content">
                    <p>{post.message}</p>
                  </div>
                  <div className="post-footer">
                    <FontAwesomeIcon icon={faThumbsUp} className="post-icon" onClick={() => handleLike(post._id)} />
                    <FontAwesomeIcon icon={faComment} className="post-icon" onClick={() => handleCommentSubmit(post._id)} />
                    <FontAwesomeIcon icon={faShare} className="post-icon" onClick={() => handleShare(post._id)} />
                  </div>
                  <div className="comment-section">
                    <textarea
                      placeholder="Write a comment..."
                      value={comment}
                      onChange={handleCommentChange}
                      className="comment-textarea"
                    />
                    <button onClick={() => handleCommentSubmit(post._id)} className="comment-button">Comment</button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>

        <div id="right-social">
          <div className="profile-summary">
            <h3>My Profile</h3>
            <img
              src={`http://localhost:5000/images/${user.picturePath}`}
              alt="Profile"
              className="profile-summary-image"
            />
            <p><strong>{user.firstName} {user.lastName}</strong></p>
            <p><FontAwesomeIcon icon={faMapMarkerAlt} className="summary-icon" /> Islamabad, Pakistan</p>
            <p><FontAwesomeIcon icon={faBriefcase} className="summary-icon" /> Frontend Engineer</p>
            <button onClick={handleSignOut} className="profile-logout-button">
              <FontAwesomeIcon icon={faSignOutAlt} className="logout-icon" /> Logout
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Socialize;
