import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import {
  faSearch, faEnvelope, faBell, faCog, faMapMarkerAlt, faBriefcase, faImage,
  faPaperclip, faFileAudio, faUserPlus, faSignOutAlt, faChevronDown, faThumbsUp,
  faComment, faShare
} from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import axios from 'axios';
import moment from 'moment';
import './Socialize.css';

const Socialize = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [posts, setPosts] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [comment, setComment] = useState('');
  const [users, setUsers] = useState([]);
  const [searchResults, setSearchResults] = useState([]);

  const { user } = useSelector(state => state.auth);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        console.log("Fetching posts...");
        const response = await axios.get('http://localhost:5000/api/posts/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        console.log("Response received:", response);
        setPosts(response.data);
        console.log("Posts set:", response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    const fetchUsers = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/auth/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        // Filter out the current user and limit to 5 users
        const filteredUsers = response.data
          .filter(u => u._id !== user._id)
          .slice(0, 5);
        setUsers(filteredUsers);
      } catch (error) {
        console.error('Error fetching users:', error);
      }
    };

    fetchPosts();
    fetchUsers();
  }, [user._id]);

  const handleSearchChange = async (event) => {
    setSearchTerm(event.target.value);
    if (event.target.value.trim()) {
      try {
        const response = await axios.get(`http://localhost:5000/api/auth/search?name=${event.target.value}`, {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setSearchResults(response.data);
      } catch (error) {
        console.error('Error searching users:', error);
      }
    } else {
      setSearchResults([]);
    }
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleSignOut = () => {
    console.log("Sign out button clicked");
  };

  const handlePostSubmit = async () => {
    if (post.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/api/posts/create',
          {
            message: post,
            firstName: user?.firstName,
            lastName: user?.lastName,
            email: user?.email,
            picturePath: user?.picturePath
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts([...posts, response.data]);
        setPost('');
      } catch (error) {
        console.error('Error creating post:', error);
      }
    }
  };

  const handleLike = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/like/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleCommentChange = (event) => {
    setComment(event.target.value);
  };

  const handleCommentSubmit = async (postId) => {
    if (comment.trim()) {
      try {
        const response = await axios.post(`http://localhost:5000/api/posts/comment/${postId}`,
          {
            userId: user?._id,
            text: comment
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts(posts.map(post => post._id === postId ? response.data : post));
        setComment('');
      } catch (error) {
        console.error('Error commenting on post:', error);
      }
    }
  };

  const handleShare = async (postId) => {
    try {
      const response = await axios.patch(`http://localhost:5000/api/posts/share/${postId}`, {}, {
        headers: { 'x-auth-token': localStorage.getItem('token') }
      });
      setPosts(posts.map(post => post._id === postId ? response.data : post));
    } catch (error) {
      console.error('Error sharing post:', error);
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
          {searchResults.length > 0 && (
            <div className="search-results">
              {searchResults.map(result => (
                <div key={result._id} className="search-result-item">
                  <img src={result.picturePath ? `http://localhost:5000/${result.picturePath}` : '/default-profile.png'} alt="Profile" />
                  <span>{result.firstName} {result.lastName}</span>
                </div>
              ))}
            </div>
          )}
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} className="top-icon" />
          <FontAwesomeIcon icon={faBell} className="top-icon" />
          <FontAwesomeIcon icon={faCog} className="top-icon" />
          <div className="user-dropdown">
            <span className="user-name" onClick={toggleDropdown}>
              {user?.firstName}
              <FontAwesomeIcon icon={faChevronDown} className="dropdown-icon" />
            </span>
            {dropdownOpen && (
              <div className="dropdown-menu">
                <button onClick={handleSignOut} className="signout-button">
                  <FontAwesomeIcon icon={faSignOutAlt} /> Sign Out
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
      <div id="mid-social">
        <div className="profile-card">
          <img src={user?.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="profile-image" />
          <h2>{user?.firstName} {user?.lastName}</h2>
          <p>{user?.friends?.length} friends</p>
          <div className="location">
            <FontAwesomeIcon icon={faMapMarkerAlt} /> {user?.location}
          </div>
          <div className="job-title">
            <FontAwesomeIcon icon={faBriefcase} /> {user?.occupation}
          </div>
          <div className="social-profiles">
            <div className="social-profile">
              <FontAwesomeIcon icon={faTwitter} /> Twitter
            </div>
            <div className="social-profile">
              <FontAwesomeIcon icon={faLinkedin} /> LinkedIn
            </div>
          </div>
        </div>
        <div className='post'>
          <div className='postMessage'>
            <div className='message'>
              <img src={user?.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="post-image" />
              <input
                type="text"
                placeholder="What's on your mind..."
                value={post}
                onChange={handlePostChange}
                id="add-post"
              />
            </div>
            <div className='post-icons'>
              <div className='icon-container'>
                <FontAwesomeIcon icon={faImage} className="post-icon" />
                <FontAwesomeIcon icon={faPaperclip} className="post-icon" />
                <FontAwesomeIcon icon={faFileAudio} className="post-icon" />
              </div>
              <button className="post-button" onClick={handlePostSubmit}>Post</button>
            </div>
          </div>
        </div>
      </div>
      <div id="bottom-social">
        <div className="posts-section">
          {posts.length === 0 ? (
            <p>Loading posts...</p>
          ) : (
            posts.map(post => (
              <div key={post._id} className="post-container">
                <div className="post-header">
                  <img src={post.picturePath ? `http://localhost:5000/${post.picturePath}` : '/default-profile.png'} alt="Profile" className="post-image" />
                  <div className="post-details">
                    <h3>{post.firstName} {post.lastName}</h3>
                    <p>{moment(post.createdAt).fromNow()}</p>
                  </div>
                </div>
                <p>{post.message}</p>
                <div className="post-actions">
                  <FontAwesomeIcon icon={faThumbsUp} className="post-action-icon" onClick={() => handleLike(post._id)} />
                  <FontAwesomeIcon icon={faComment} className="post-action-icon" />
                  <FontAwesomeIcon icon={faShare} className="post-action-icon" onClick={() => handleShare(post._id)} />
                </div>
                <div className="comments-section">
                  {post.comments.map(comment => (
                    <div key={comment._id} className="comment">
                      <p><strong>{comment.userId}</strong>: {comment.text}</p>
                    </div>
                  ))}
                  <div className="comment-input">
                    <input
                      type="text"
                      placeholder="Write a comment..."
                      value={comment}
                      onChange={handleCommentChange}
                    />
                    <button onClick={() => handleCommentSubmit(post._id)}>Submit</button>
                  </div>
                </div>
              </div>
            ))
          )}
        </div>
        <div className="users-section">
          <h3>Users</h3>
          {users.length === 0 ? (
            <p>No users found</p>
          ) : (
            users.map(user => (
              <div key={user._id} className="user-card">
                <img src={user.picturePath ? `http://localhost:5000/${user.picturePath}` : '/default-profile.png'} alt="Profile" className="user-image" />
                <h4>{user.firstName} {user.lastName}</h4>
                <FontAwesomeIcon icon={faUserPlus} className="user-add-icon" />
              </div>
            ))
          )}
        </div>
      </div>
    </div>
  );
};

export default Socialize;
