import React, { useState, useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
import { signup } from '../actions/authActions';
import './Signup.css';

const Signup = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    location: '',
    occupation: '',
    picturePath: '',
  });

  const [fileInputKey, setFileInputKey] = useState(Date.now()); // Ensure file input key changes on file selection
  const dispatch = useDispatch();
  const navigate = useNavigate();
  const { error, isAuthenticated } = useSelector((state) => state.auth);

  const handleChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFormData({ ...formData, picturePath: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    await dispatch(signup(formData)); // Wait for signup action to complete

    if (isAuthenticated) {
      navigate('/signin'); // Navigate to signin page if authenticated
    }
  };

  useEffect(() => {
    if (isAuthenticated) {
      navigate('/signin');
    }
  }, [isAuthenticated, navigate]);

  return (
    <div className="outerContainer">
      <div className="signupContainer">
        <h1 className="header">Sociopedia</h1>
        <h2 className="subtitle">Welcome to Sociopedia, the Social Media For Sociopaths!</h2>
        {error && <p className="error">{error}</p>}
        <form onSubmit={handleSubmit} className="form">
          <div className="row">
            <input
              type="text"
              placeholder="First Name"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              className="input"
              required
            />
            <input
              type="text"
              placeholder="Last Name"
              name="lastName"
              value={formData.lastName}
              onChange={handleChange}
              className="input"
              required
            />
          </div>
          <input
            type="text"
            placeholder="Location"
            name="location"
            value={formData.location}
            onChange={handleChange}
            className="input"
          />
          <input
            type="text"
            placeholder="Occupation"
            name="occupation"
            value={formData.occupation}
            onChange={handleChange}
            className="input"
          />
          <div className="fileUpload">
            <input
              type="file"
              onChange={handleFileChange}
              key={fileInputKey} // Ensure key changes on file input change
              className="fileInput"
            />
            <button type="button" onClick={() => setFileInputKey(Date.now())} className="uploadButton">
              Upload
            </button>
            <span className="filePath">{formData.picturePath}</span>
          </div>
          <input
            type="email"
            placeholder="Email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            className="input"
            required
          />
          <input
            type="password"
            placeholder="Password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            className="input"
            required
          />
          <button type="submit" className="button">REGISTER</button>
        </form>
        <p className="loginText">
          Already have an account? <a href="/signin" className="loginLink">Login here.</a>
        </p>
      </div>
    </div>
  );
};

export default Signup;
