import React, { useState, useEffect } from 'react';
import { useSelector } from 'react-redux';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEnvelope, faBell, faCog, faMapMarkerAlt, faBriefcase, faImage, faPaperclip, faFileAudio, faUserPlus, faSignOutAlt, faChevronDown, faHeart, faShare, faComment } from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import axios from 'axios';
import './Socialize.css';

const Socialize = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setPost] = useState('');
  const [image, setImage] = useState(null);
  const [posts, setPosts] = useState([]);
  const [dropdownOpen, setDropdownOpen] = useState(false);
  const [commentText, setCommentText] = useState('');
  const [selectedPostId, setSelectedPostId] = useState(null);

  const { user } = useSelector(state => state.auth);

  useEffect(() => {
    const fetchPosts = async () => {
      try {
        const response = await axios.get('http://localhost:5000/api/posts/all', {
          headers: { 'x-auth-token': localStorage.getItem('token') }
        });
        setPosts(response.data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    };

    fetchPosts();
  }, []);

  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };

  const handlePostChange = (event) => {
    setPost(event.target.value);
  };

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        setImage({
          data: reader.result.split(',')[1], // Base64 encoded image data
          contentType: file.type
        });
      };
      reader.readAsDataURL(file);
    }
  };

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };

  const handleSignOut = () => {
    console.log("Sign out button clicked");
  };

  const handlePostSubmit = async () => {
    if (post.trim()) {
      try {
        const response = await axios.post('http://localhost:5000/api/posts/create', 
          {
            message: post,
            firstName: user.firstName,
            lastName: user.lastName,
            email: user.email,
            image: image
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        setPosts([...posts, response.data]);
        setPost('');
        setImage(null);
      } catch (error) {
        console.error('Error creating post:', error);
      }
    }
  };

  const handleCommentChange = (event) => {
    setCommentText(event.target.value);
  };

  const handleAddComment = async (postId) => {
    if (commentText.trim()) {
      try {
        const response = await axios.post(`http://localhost:5000/api/posts/comment/${postId}`, 
          {
            userId: user._id,
            text: commentText
          },
          { headers: { 'x-auth-token': localStorage.getItem('token') } }
        );
        const updatedPosts = posts.map(post => post._id === postId ? response.data : post);
        setPosts(updatedPosts);
        setCommentText('');
      } catch (error) {
        console.error('Error adding comment:', error);
      }
    }
  };

  const handleLike = async (postId) => {
    try {
      const response = await axios.post(`http://localhost:5000/api/posts/like/${postId}`, 
        {
          userId: user._id
        },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      const updatedPosts = posts.map(post => post._id === postId ? response.data : post);
      setPosts(updatedPosts);
    } catch (error) {
      console.error('Error liking post:', error);
    }
  };

  const handleShare = async (postId) => {
    try {
      const response = await axios.post(`http://localhost:5000/api/posts/share/${postId}`, 
        {
          userId: user._id
        },
        { headers: { 'x-auth-token': localStorage.getItem('token') } }
      );
      const updatedPosts = posts.map(post => post._id === postId ? response.data : post);
      setPosts(updatedPosts);
    } catch (error) {
      console.error('Error sharing post:', error);
    }
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} className="top-icon" />
          <FontAwesomeIcon icon={faBell} className="top-icon" />
          <FontAwesomeIcon icon={faCog} className="top-icon" />
          <div className="user-dropdown">
            <span className="user-name" onClick={toggleDropdown}>
              {user && user.firstName}
              <FontAwesomeIcon icon={faChevronDown} className="dropdown-icon" />
            </span>
            {dropdownOpen && (
              <div className="dropdown-menu">
                <button onClick={handleSignOut} className="signout-button">
                  <FontAwesomeIcon icon={faSignOutAlt} /> Sign Out
                </button>
              </div>
            )}
          </div>
        </div>
      </div>
      <div id="mid-social">
        <div className="profile-card">
          <img src={user && `data:${user.picture.contentType};base64,${user.picture.data}`} alt="Profile" className="profile-image" />
          <h2>{user && `${user.firstName} ${user.lastName}`}</h2>
          <p>{user && user.friends.length} friends</p>
          <div className="location">
            <FontAwesomeIcon icon={faMapMarkerAlt} /> {user && user.location}
          </div>
          <div className="job-title">
            <FontAwesomeIcon icon={faBriefcase} /> {user && user.occupation}
          </div>
          <div className="social-profiles">
            <div className="social-profile">
              <FontAwesomeIcon icon={faTwitter} /> Twitter
            </div>
            <div className="social-profile">
              <FontAwesomeIcon icon={faLinkedin} /> LinkedIn
            </div>
          </div>
        </div>
        <div className='post'>
          <div className='message'>
            <img src={user && `data:${user.picture.contentType};base64,${user.picture.data}`} alt="Profile" className="post-image" />
            <input
              type="text"
              placeholder="What's on your mind..."
              value={post}
              onChange={handlePostChange}
              id="add-post"
            />
            <input
              type="file"
              accept="image/*"
              onChange={handleImageChange}
              className="image-upload"
            />
          </div>
          <div className='post-icons'>
            <div className='icon-container'>
              <FontAwesomeIcon icon={faImage} className="post-icon" />
              <FontAwesomeIcon icon={faPaperclip} className="post-icon" />
              <FontAwesomeIcon icon={faFileAudio} className="post-icon" />
            </div>
            <button className="post-button" onClick={handlePostSubmit}>Post</button>
          </div>
        </div>
        <div className='posts-list'>
          {posts.map((post) => (
            <div key={post._id} className='post-item'>
              <strong>{post.firstName} {post.lastName}:</strong> {post.message}
              {post.image && (
                <img
                  src={`data:${post.image.contentType};base64,${post.image.data}`}
                  alt="Post"
                  className="post-image"
                />
              )}
              <div className='post-actions'>
                <button onClick={() => handleLike(post._id)}>
                  <FontAwesomeIcon icon={faHeart} /> {post.likes.length}
                </button>
                <button onClick={() => setSelectedPostId(post._id)}>
                  <FontAwesomeIcon icon={faComment} /> {post.comments.length}
                </button>
                <button onClick={() => handleShare(post._id)}>
                  <FontAwesomeIcon icon={faShare} /> {post.shares}
                </button>
              </div>
              {selectedPostId === post._id && (
                <div className='comments-section'>
                  <input
                    type="text"
                    placeholder="Add a comment..."
                    value={commentText}
                    onChange={handleCommentChange}
                  />
                  <button onClick={() => handleAddComment(post._id)}>Comment</button>
                  {post.comments.map(comment => (
                    <div key={comment._id} className='comment-item'>
                      <strong>{comment.userId}</strong>: {comment.text}
                    </div>
                  ))}
                </div>
              )}
            </div>
          ))}
        </div>
        <div className='add-list'>
          <h2>People you may know</h2>
          <div className='add'>
            <img src="/image.png" alt="Profile" className="add-image" />
            <div className="add-info">
              <h3>hahaha</h3>
              <p>Islamabad, Pakistan</p>
            </div>
            <FontAwesomeIcon icon={faUserPlus} className="add-icon" />
          </div>
          <div className='add'>
            <img src="/unnamed.jpg" alt="Profile" className="add-image" />
            <div className="add-info">
              <h3>raz</h3>
              <p>Karachi, Pakistan</p>
            </div>
            <FontAwesomeIcon icon={faUserPlus} className="add-icon" />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Socialize;
