import React, { useState } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faSearch, faEnvelope, faBell, faCog } from '@fortawesome/free-solid-svg-icons';
import { faMapMarkerAlt, faBriefcase } from '@fortawesome/free-solid-svg-icons';
import { faTwitter, faLinkedin } from '@fortawesome/free-brands-svg-icons';
import './Socialize.css';

const Socialize = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [post, setpost] = useState('');


  const handleSearchChange = (event) => {
    setSearchTerm(event.target.value);
  };
  const handlePostChange = (event) => {
    setpost(event.target.value);
  };

  return (
    <div id="outer-social">
      <div id="top-social">
        <h1>Socialize</h1>
        <div className="search-container">
          <input
            type="text"
            placeholder="Search..."
            value={searchTerm}
            onChange={handleSearchChange}
            id="search-bar"
          />
          <FontAwesomeIcon icon={faSearch} className="search-icon" />
        </div>
        <div className="icon-container">
          <FontAwesomeIcon icon={faEnvelope} className="top-icon" />
          <FontAwesomeIcon icon={faBell} className="top-icon" />
          <FontAwesomeIcon icon={faCog} className="top-icon" />
          <span className="user-name">Hassan</span>
        </div>
      </div>
      <div id="mid-social">
        <div className="profile-card">
          <img src="/image.png" alt="Profile" className="profile-image" />
          <h2>Fake Man</h2>
          <p>2 friends</p>
          <div className="location">
            <FontAwesomeIcon icon={faMapMarkerAlt} /> Fake Location, CA
          </div>
          <div className="job-title">
            <FontAwesomeIcon icon={faBriefcase} /> Fake Con Man
          </div>
          <div className="stats">
            <p>Who's viewed your profile: 7637</p>
            <p>Impressions of your post: 7425</p>
          </div>
          <div className="social-profiles">
            <div className="social-profile">
              <FontAwesomeIcon icon={faTwitter} /> Twitter
            </div>
            <div className="social-profile">
              <FontAwesomeIcon icon={faLinkedin} /> LinkedIn
            </div>
          </div>
        </div>
        <div className='post'>
        <div className='message'>
        <img src="/image.png" alt="Profile" className="post-image" />
        <input
            type="text"
            placeholder="    Whats's on your mind..."
            value={post}
            onChange={handlePostChange}
            id="add-post"
          />
        </div>
        <div className='post-icons'>
          
          </div>
        </div>
      </div>
    </div>
  );
}

export default Socialize;
